import React, {useState, useEffect} from 'react';
import { Card, CardBody, Form, FormGroup, Input, Button } from 'reactstrap';
import { Image, Navbar, Col, Row} from 'react-bootstrap';
import './style.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import '@fortawesome/fontawesome-free';
import { Link } from 'react-router-dom';
import envelope from '../../img/envelope-solid.svg';
import calender from '../../img/Group87.svg';
import { uploads } from '../../services';

const ComingSoonRegister = () => {
    const [isLoading, setLoading] = useState(false);
    const [countDown, setCountDown] = useState({
        days: '',
        hours: '',
        minutes: '',
        seconds: '',
    });
    const [userPost, setUserPost] = useState({
        email: '',
        username: '',
        password: '',
        password2: '',
    });
    var countDownDate = new Date("Jan 1, 2022 00:00:00").getTime();

    const fixing = ((input) => {
        if (input < 10) {
            return '0' + input;
        } else {
            return input;
        }
    })

    useEffect(() => {
        const timer = setInterval(() => {
            var now = new Date().getTime();
            var distance = countDownDate - now;

            var days = Math.floor(distance / (1000 * 60 * 60 * 24));
            var hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
            var minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
            var seconds = Math.floor((distance % (1000 * 60)) / 1000);
            const fixhours = fixing(hours);
            const fixminutes = fixing(minutes);
            const fixseconds = fixing(seconds);
            
            setCountDown({days:days, hours:fixhours, minutes:fixminutes, seconds:fixseconds});
        }, 1000);
        return () => {
            clearInterval(timer);
        }
    }, []);
    
    const changeHandler = (e) => {
        setUserPost({ ...userPost, [e.target.id]: e.target.value });
    }

    const submitUser = async() => {
        setLoading(true);
        const register = await uploads.register(userPost);
        console.log(register.statusCode);
        if (register.statusCode == 201) {
            setLoading(false);
            alert(register.message);
            window.location.replace('/');
        } else {
            setLoading(false);
            alert(register.message);
        }
    }

    return (
        <div>
            {/* <Row> */}
                <Col md={4}>
                    <Card className="text-center" style={{backgroundColor:'white', borderRadius:'0 10px 10px 0', height:'100vh', boxShadow:'0 8px 8px 0 rgba(0, 0, 0, 0.2)'}}>
                        <CardBody>
                            <Navbar style={{marginTop:'-20px'}}>
                                <Navbar.Brand href="#home" style={{width:'100%'}}>
                                    <Image src="./img/KIPLogo.svg" style={{height:'60px'}}></Image>
                                </Navbar.Brand>
                            </Navbar>
                            <div style={{marginTop:'-3%'}}>
                                <table style={{margin:'0 auto'}}>
                                    <tr className="countDownRegister">
                                        <td><span>{countDown.days}</span></td>
                                        <td><span>:</span></td>
                                        <td><span>{countDown.hours}</span></td>
                                        <td><span>:</span></td>
                                        <td><span>{countDown.minutes}</span></td>
                                        <td><span>:</span></td>
                                        <td><span>{countDown.seconds}</span></td>
                                    </tr>
                                    <tr className="countDownRegisterTitle">
                                        <td><span>DAYS</span></td>
                                        <td></td>
                                        <td><span>HOURS</span></td>
                                        <td></td>
                                        <td><span>MINUTES</span></td>
                                        <td></td>
                                        <td><span>SECONDS</span></td>
                                    </tr>
                                </table>
                                <div style={{marginBottom:'3%'}}>
                                    <Image src={calender} style={{width:'80%', marginTop:'20px'}} />
                                    <p style={{fontWeight:'700', marginTop:'20px'}}>Weâ€™re Coming Soon</p>
                                    <p style={{marginTop:'-20px'}}>We'll come up with something new!</p>
                                    <Link to='/' style={{backgroundColor:'#FFA740', border:'0px', color:'#212121', fontWeight:'700', padding:'7px 10%', borderRadius:'5px', textDecoration:'none'}}>HOME</Link>
                                </div>
                            </div>
                        </CardBody>
                    </Card>
                </Col>
                {/* <Col>
                    <Form>
                        <FormGroup>
                            <h5 className="data">Email</h5>
                            <Input 
                                type="text"
                                name="email"
                                id="email"
                                placeholder="Insert your Email"
                                onChange={changeHandler}
                            />
                        </FormGroup>
                        <FormGroup>
                            <h5 className="data">Username</h5>
                            <Input 
                                type="text"
                                name="username"
                                id="username"
                                placeholder="Insert your Username"
                                onChange={changeHandler}
                            />
                        </FormGroup>
                        <FormGroup>
                            <h5 className="data">Password</h5>
                            <Input
                                type="password"
                                name="password"
                                id="password"
                                placeholder="Insert Your Password"
                                onChange={changeHandler}
                            />
                        </FormGroup>
                        <FormGroup>
                            <h5 className="data">Confirmation Password</h5>
                            <Input
                                type="password"
                                name="password2"
                                id="password2"
                                placeholder="Confirm Your Password"
                                onChange={changeHandler}
                            />
                        </FormGroup>
                        <div className="buttonRegister">
                            <Button
                                color="primary"
                                onClick={(e) => {
                                    e.preventDefault();
                                    submitUser();
                                }}
                                disabled={isLoading}
                            >
                                Register
                            </Button>
                        </div>
                    </Form>
                </Col> */}
            {/* </Row> */}
        </div>
    )
};

export default ComingSoonRegister;
