import React, {useState, useEffect} from 'react';
import {
  Card,
  CardBody,
} from 'reactstrap';
import { Image, Navbar, Button } from 'react-bootstrap';
import './style.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import '@fortawesome/fontawesome-free';
import { Link } from 'react-router-dom';
import envelope from '../../img/envelope-solid.svg';
import calender from '../../img/Group84.svg';

const ComingSoon = () => {
    const [countDown, setCountDown] = useState({
        days: '',
        hours: '',
        minutes: '',
        seconds: '',
    });
    var countDownDate = new Date("Jan 1, 2022 00:00:00").getTime();

    const fixing = ((input) => {
        if (input < 10) {
            return '0' + input;
        } else {
            return input;
        }
    })

    useEffect(() => {
        const timer = setInterval(() => {
            var now = new Date().getTime();
            var distance = countDownDate - now;

            var days = Math.floor(distance / (1000 * 60 * 60 * 24));
            var hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
            var minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
            var seconds = Math.floor((distance % (1000 * 60)) / 1000);
            const fixhours = fixing(hours);
            const fixminutes = fixing(minutes);
            const fixseconds = fixing(seconds);
            
            setCountDown({days:days, hours:fixhours, minutes:fixminutes, seconds:fixseconds});
        }, 1000);
        return () => {
            clearInterval(timer);
        }
    }, []);

    console.log(countDown);

    return (
        <div>
            <Card className="text-center" style={{width:'80%', margin:'auto', marginTop:'5%', backgroundColor:'white', borderRadius:'10px'}}>
                <CardBody>
                    <Navbar style={{marginTop:'-20px'}}>
                        <Navbar.Brand href="#home">
                            <Image src="./img/KIPLogo.svg" style={{height:'60px', marginLeft:'20px'}}></Image>
                        </Navbar.Brand>
                        <Navbar.Toggle />
                        <Navbar.Collapse className="justify-content-end">
                            <Navbar.Text>
                                customer.service@kiptour.com
                                <Image src={envelope} height='20px' style={{marginLeft:'10px'}} />
                            </Navbar.Text>
                        </Navbar.Collapse>
                    </Navbar>
                    <div style={{marginTop:'-3%'}}>
                        <table style={{margin:'0 auto'}}>
                            <tr className="countDown">
                                <td><span>{countDown.days}</span></td>
                                <td><span>:</span></td>
                                <td><span>{countDown.hours}</span></td>
                                <td><span>:</span></td>
                                <td><span>{countDown.minutes}</span></td>
                                <td><span>:</span></td>
                                <td><span>{countDown.seconds}</span></td>
                            </tr>
                            <tr className="countDownTitle">
                                <td><span>DAYS</span></td>
                                <td></td>
                                <td><span>HOURS</span></td>
                                <td></td>
                                <td><span>MINUTES</span></td>
                                <td></td>
                                <td><span>SECONDS</span></td>
                            </tr>
                        </table>
                        <div style={{marginBottom:'2%'}}>
                            <Image src={calender} style={{width:'90%', marginTop:'20px'}} />
                            <p style={{fontWeight:'700', marginTop:'20px'}}>Weâ€™re Coming Soon</p>
                            <p style={{marginTop:'-20px'}}>We'll come up with something new!</p>
                            <Button style={{backgroundColor:'#FFA740', color:'#212121', fontWeight:'700', width:'30%'}}>JOIN US</Button>
                        </div>
                    </div>
                </CardBody>
            </Card>
        </div>
    )
};

export default ComingSoon;
